mapper.insert:
INSERT INTO PRODUCT_INFO
(PRODUCT_CODE
, PRODUCT_NAME
, ORIGIN_COST
, RELEASE_DATE
, DISCOUNT_RATE
, SALES_QUANTITY
, STOCK_QUANTITY
, CATEGORY_CODE
, PRODUCTION_STATUS)
VALUES (#{productCode}
       , #{productName}
       , #{originCost}
       , #{releaseDate}
       , #{discountRate}
       , #{salesQuantity}
       , #{stockQuantity}
       , #{categoryCode}
       , #{productionStatus})

mapper.update:
update PRODUCT_INFO
SET SALES_QUANTITY = #{salesQuantity}
  , PRODUCTION_STATUS = #{productionStatus}
WHERE PRODUCT_CODE = #{productCode}
